<script setup lang="ts">
import { useNuxtApp } from "#app";
import Select from "primevue/select";
import LocationParams from "~/features/ListingFeature/Core/Params/location_params";

const { $googleMaps } = useNuxtApp();

const location = ref<LocationParams>(
  new LocationParams(-34.3974, 150.644, "", "", "", ""),
);

const cities = ref([
  { name: "New York", code: "NY" },
  { name: "Rome", code: "RM" },
  { name: "London", code: "LDN" },
  { name: "Istanbul", code: "IST" },
  { name: "Paris", code: "PRS" },
]);

const emit = defineEmits<{
  (e: "update:location", location: LocationParams): void;
}>();

watch(
  () => location.value,
  () => {
    // console.log(basicInformation.value);
    emit("update:location", location.value);
  },
  { deep: true },
);

const initMap = async () => {
  try {
    const google = await $googleMaps.load();
    const map = new google.maps.Map(
      document.getElementById("map") as HTMLElement,
      {
        center: { lat: location.value.latitude, lng: location.value.longitude },
        zoom: 8,
      },
    );
    const marker = new google.maps.Marker({
      position: { lat: location.value.latitude, lng: location.value.longitude },
      map,
      draggable: true, // Makes the marker draggable
      icon: {
        url: "data:image/png;base64,", // Custom icon URL
        scaledSize: new google.maps.Size(50, 50), // Resize the icon
      },
    });

    marker.addListener("dragend", (event: Event) => {
      location.value.latitude = event.latLng.lat();
      location.value.longitude = event.latLng.lng();
      fetchAddress(location.value.latitude, location.value.longitude);
    });
  } catch (error) {
    console.error("Error loading Google Maps:", error);
  }
};

// Function to fetch the address using the Geocoding API
const fetchAddress = async (
  lat: number,
  lng: number,
): Promise<string | null> => {
  try {
    const response = await fetch(
      `https://maps.googleapis.com/maps/api/geocode/json?latlng=${lat},${lng}&key=AIzaSyAQ7_UczCJUFVV-KYU-VlVB3o7OuAhY4oU`,
    );
    const data = await response.json();

    if (data.status === "OK") {
      const result = data.results;
      console.log("Geocoded data:", result);
      return result.formatted_address; // Return the formatted address
    } else {
      console.error("Geocoding failed:", data.status);
      return null;
    }
  } catch (error) {
    console.error("Error fetching address:", error);
    return null;
  }
};

onMounted(() => {
  initMap();
});
</script>

